import React, { useState } from 'react';

const AddBookForm = () => {
  const [book, setBook] = useState({
    title: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setBook({ ...book, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Create a single DTO for the book title
      const bookDto = {
        title: book.title,
      };

      // Send a POST request with the Book DTO using fetch
      await fetch('http://localhost:8080/book/add', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(bookDto),
      });

      // Clear the form after successful submission
      setBook({
        title: '',
      });

      console.log('Book title added successfully!');
    } catch (error) {
      console.error('Error adding book title:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>Title:</label>
      <input type="text" name="title" value={book.title} onChange={handleChange} />

      <button type="submit">Add Book Title</button>
    </form>
  );
};

export default AddBookForm;
