import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const BookList = () => {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    // Simulate fetching books from an API
    fetch('http://localhost:8080/book/all')
      .then(response => response.json())
      .then(data => setBooks(data))
      .catch(error => console.error('Error fetching books:', error));
  }, []); // Empty dependency array ensures the effect runs once on mount

  return (
    <div>
      <h2>Book List</h2>
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(3, 1fr)', gap: '16px' }}>
        {books.map(book => (
          <Link key={book.id} to={`/book/${book.id}`} style={{ textDecoration: 'none', color: 'inherit' }}>
            <div style={{ border: '1px solid #ccc', padding: '16px', borderRadius: '8px', cursor: 'pointer' }}>
              <h3>{book.title}</h3>
              <p>Author: {book.author}</p>
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default BookList;
