import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const BookList = () => {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchInput, setSearchInput] = useState('');
  const [filteredBooks, setFilteredBooks] = useState([]);
  const [sortBy, setSortBy] = useState('title'); // Default sorting by title
  const [filterBy, setFilterBy] = useState('all'); // Default filtering option

  useEffect(() => {
    // Simulate fetching books from an API
    fetch('http://localhost:8080/book/all')
      .then(response => response.json())
      .then(data => {
        setBooks(data);
        setFilteredBooks(data); // Initialize filtered books with all books on mount
      })
      .catch(error => setError(error))
      .finally(() => setLoading(false));
  }, []); // Empty dependency array ensures the effect runs once on mount

  const fetchReviewsAndCalculateAverage = async bookId => {
    try {
      const response = await fetch(`http://localhost:8080/review/book/${bookId}`);
      const reviews = await response.json();

      if (reviews.length > 0) {
        const averageRating = reviews.reduce((sum, review) => sum + review.rating, 0) / reviews.length;
        return averageRating.toFixed(1); // Display average rating with one decimal place
      } else {
        return 'N/A'; // Display 'N/A' if there are no reviews
      }
    } catch (error) {
      console.error('Error fetching reviews:', error.message);
      return 'Error'; // Display 'Error' if there is an error fetching reviews
    }
  };

  const updateBooksAverageRating = async () => {
    const updatedBooks = await Promise.all(
      books.map(async book => {
        const averageRating = await fetchReviewsAndCalculateAverage(book.id);
        return { ...book, averageRating };
      })
    );

    //setFilteredBooks(updatedBooks);
  };

  useEffect(() => {
    // Update average ratings when books change
    updateBooksAverageRating();
  }, [books]);

  const handleSearch = () => {
    const lowercasedInput = searchInput.toLowerCase();
    const filtered = books.filter(
      book =>
        book.title.toLowerCase().includes(lowercasedInput) ||
        book.authors.some(
          author =>
            author.firstName.toLowerCase().includes(lowercasedInput) ||
            author.lastName.toLowerCase().includes(lowercasedInput)
        )
    );
    setFilteredBooks(filtered);
  };

  const handleSortChange = e => {
    setSortBy(e.target.value);
    handleSort(e.target.value);
  };

  const handleSort = sortByOption => {
    const sorted = [...filteredBooks].sort((a, b) => {
      if (sortByOption === 'title') {
        return a.title.localeCompare(b.title);
      } else if (sortByOption === 'author') {
        const authorA = a.authors.map(author => author.lastName).join('');
        const authorB = b.authors.map(author => author.lastName).join('');
        return authorA.localeCompare(authorB);
      } else {
        // Add more sorting options as needed
        return 0;
      }
    });
    setFilteredBooks(sorted);
  };

  const handleFilterChange = e => {
    setFilterBy(e.target.value);
    handleFilter(e.target.value);
  };

  const handleFilter = filterOption => {
    if (filterOption === 'all') {
      setFilteredBooks(books);
    } else {
      const filtered = books.filter(book => book.publisher.name.toLowerCase() === filterOption.toLowerCase());
      setFilteredBooks(filtered);
    }
  };

  const handleInputChange = e => {
    setSearchInput(e.target.value);
    handleSearch(); // Trigger search on every input change
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error fetching books: {error.message}</div>;
  }

  return (
    <div>
      <h2>Book List</h2>
      <div>
        <input
          type="text"
          placeholder="Search by name or author"
          value={searchInput}
          onChange={handleInputChange}
        />
        <button onClick={handleSearch}>Search</button>
      </div>
      <div>
        <label htmlFor="sort">Sort By:</label>
        <select id="sort" value={sortBy} onChange={handleSortChange}>
          <option value="title">Title</option>
          <option value="author">Author</option>
          {/* Add more sorting options as needed */}
        </select>
      </div>
      <div>
        <label htmlFor="filter">Filter By Publisher:</label>
        <select id="filter" value={filterBy} onChange={handleFilterChange}>
          <option value="all">All</option>
          {/* Add unique publisher names from your data */}
          <option value="Polirom">Polirom</option>
          <option value="publisher2">Publisher 2</option>
          {/* Add more publisher options as needed */}
        </select>
      </div>
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(3, 1fr)', gap: '16px' }}>
        {filteredBooks.map(book => (
          <div key={book.id}>
            <Link to={`/book/${book.id}`} style={{ textDecoration: 'none', color: 'inherit' }}>
              <div style={{ border: '1px solid #ccc', padding: '16px', borderRadius: '8px', cursor: 'pointer' }}>
                <h3>{book.title}</h3>
                <p>ID: {book.id}</p>
                <p>
                  Authors: {book.authors.map(author => `${author.firstName} ${author.lastName}`).join(', ')}
                </p>
                <p>Publisher: {book.publisher.name}</p>
                <p>Average Rating: {book.averageRating}</p>
              </div>
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
};

export default BookList;
