import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const IndividualBookPage = () => {
  const { id } = useParams();
  const [book, setBook] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [loadingReviews, setLoadingReviews] = useState(true);

  // Get the username from local storage
  const storedUsername = localStorage.getItem('username');

  // State for new review
  const [newReview, setNewReview] = useState({
    user: storedUsername || "", // Use the stored username, or an empty string if not available
    rating: 0,
    text: "",
  });

  useEffect(() => {
    // Fetch individual book details based on the ID
    fetch(`http://localhost:8080/book/${id}`)
      .then(response => response.json())
      .then(data => setBook(data))
      .catch(error => console.error(`Error fetching book ${id} details:`, error));

    // Fetch reviews for the individual book using the updated endpoint
    fetch(`http://localhost:8080/review/book/${id}`)
      .then(response => response.json())
      .then(data => setReviews(data))
      .catch(error => console.error(`Error fetching reviews for book ${id}:`, error))
      .finally(() => setLoadingReviews(false));
  }, [id]);

  const handleReviewSubmit = async (e) => {
    e.preventDefault();

    try {
      // Send the new review to the server
      const response = await fetch(`http://localhost:8080/review/add`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newReview),
      });

      // Handle the response as needed
      // ...

      // Refresh the reviews after submitting a new one
      const updatedReviews = await response.json();
      setReviews(updatedReviews);

      // Clear the new review form
      setNewReview({
        user: storedUsername || "", // Use the stored username, or an empty string if not available
        rating: 0,
        text: "",
      });
    } catch (error) {
      console.error('Error submitting review:', error);
      // Handle error as needed
    }
  };

  if (!book || loadingReviews) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h2>{book.title}</h2>
      <p>Author: {book.authors.map(author => `${author.firstName} ${author.lastName}`).join(', ')}</p>
      {book.publisher && (
        <p>Publisher: {book.publisher.name}</p>
      )}

      <h3>Reviews:</h3>
      {reviews.length === 0 ? (
        <p>No reviews available for this book.</p>
      ) : (
        <ul>
          {reviews.map(review => (
            <li key={review.id}>
              <p>User: {review.user.username}</p>
              <p>Rating: {review.rating}</p>
              <p>{review.text}</p>
              <p>Date: {review.date}</p>
            </li>
          ))}
        </ul>
      )}

      {/* Form for adding a new review */}
      <h3>Add a Review:</h3>
      <form onSubmit={handleReviewSubmit}>
        {/* Remove the user input field as it's handled with the stored username */}
        {/* <label>User:</label>
        <input
          type="text"
          value={newReview.user}
          onChange={(e) => setNewReview({ ...newReview, user: e.target.value })}
          required
        /> */}

        <label>Rating:</label>
        <input
          type="number"
          value={newReview.rating}
          onChange={(e) => setNewReview({ ...newReview, rating: e.target.value })}
          required
        />

        <label>Review:</label>
        <textarea
          value={newReview.text}
          onChange={(e) => setNewReview({ ...newReview, text: e.target.value })}
          required
        />

        <button type="submit">Submit Review</button>
      </form>
    </div>
  );
};

export default IndividualBookPage;
