import React, { useEffect, useState } from 'react';
import { Typography, Paper, Button, TextField } from '@mui/material';
import axios from 'axios';

const Profile = () => {
  const [userData, setUserData] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [editedUserData, setEditedUserData] = useState({});
  const username = localStorage.getItem('username');

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await axios.get(
          `http://localhost:8080/user/findByUsername/${username}`
        );

        setUserData(response.data);
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

      if (username) {
      fetchUserData();
    }
  }, [username]);

  const handleEditClick = () => {
    setEditMode(true);
    setEditedUserData(userData);
  };

  const handleCancelEdit = () => {
    setEditMode(false);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditedUserData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSaveChanges = async () => {
    try {
      await axios.put(`http://localhost:8080/user/update/${userData.id}`, editedUserData);
      setEditMode(false);
      // Refetch user data after update
      const response = await axios.get(`http://localhost:8080/user/findByUsername/${username}`);
      setUserData(response.data);
    } catch (error) {
      console.error('Error updating user data:', error);
      // Handle error as needed
    }
  };

  const [showPassword, setShowPassword] = useState(false);

  return (
    <div style={{ padding: '20px' ,display:'flex',alignContent:'center',alignItems:'center',background:'',height:'100vh'}}>
      <Typography variant="h3" gutterBottom style = {{margin:'20px',position:'relative',top:'-45vh',left:'600px'}}>
        My Profile
      </Typography>
      {userData ? (
        <Paper elevation={0} style={{ padding: '20px', maxWidth: '400px' }}>
          {editMode ? (
            <>
              <TextField
                label="Username"
                name="username"
                value={editedUserData.username}
                onChange={handleInputChange}
                fullWidth
              />
              <TextField
                label="E-mail"
                name="email"
                value={editedUserData.email}
                onChange={handleInputChange}
                fullWidth
              />
              <TextField
                label="Password"
                name="password"
                value={editedUserData.password}
                onChange={handleInputChange}
                fullWidth
              />
              <TextField
                label="Age"
                name="age"
                type="number"
                value={editedUserData.age}
                onChange={handleInputChange}
                fullWidth
              />
              {/* Add more fields as needed */}
              <Button variant="contained" color="primary" onClick={handleSaveChanges}>
                Save Changes
              </Button>
              <Button variant="outlined" color="secondary" onClick={handleCancelEdit}>
                Cancel
              </Button>
            </>
          ) : (
            <Typography variant="h6" gutterBottom style = {{margin:'20px',position:'relative',top:'-15vh',left:'400px'}}>
              <p>Username: {userData.username}</p>
              <p>E-mail: {userData.email}</p>

                  <div>
                    <p>
                      Password: {showPassword ? userData.password : '******'}
                      <button onClick={() => setShowPassword(!showPassword)}>
                        {showPassword ? 'Hide Password' : 'Show Password'}
                      </button>
                    </p>
                  </div>

              <p type={showPassword ? 'text' : 'password'}
                      value={userData.password}
                      readOnly>Password: {userData.password}</p>
              <p>Age: {userData.age}</p>
              <p>Roles: {userData.roles.map((role, index) => (index === 0 ? role.roleName : `, ${role.roleName}`))}</p>
            </Typography>
          )}
        </Paper>
      ) : (
        <p>Please Login to see your Profile.</p>
      )}
      <br />
      {!editMode && (
        <Button variant="contained" color="primary" onClick={handleEditClick}>
          Edit Profile
        </Button>
      )}
    </div>
  );
};

export default Profile;
