import React, { useState } from 'react';

const AddBookForm = () => {
  const [book, setBook] = useState({
    title: '',
    authors: new Set(),
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setBook({ ...book, [name]: value });
  };

  const handleAuthorsChange = (e) => {
    const authorsInput = e.target.value;
    const authorsArray = authorsInput.split(',').map((author) => {
      const [firstName, ...rest] = author.trim().split(' ');
      const lastName = rest.join(' ').trim();
      return { firstName, lastName };
    });
    setBook((prevBook) => ({ ...prevBook, authors: new Set(authorsArray) }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Create a single DTO for the entire Book entity
      const bookDto = {
        title: book.title,
        authors: Array.from(book.authors),
      };

      // Send a POST request with the Book DTO using fetch
      await fetch('http://localhost:8080/book/add', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(bookDto),
      });

      // Clear the form after successful submission
      setBook({
        title: '',
        authors: new Set(),
      });

      console.log('Book added successfully!');
    } catch (error) {
      console.error('Error adding book:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>Title:</label>
      <input type="text" name="title" value={book.title} onChange={handleChange} />

      <label>Authors (comma-separated, first and last name):</label>
      <input type="text" name="authors" value={Array.from(book.authors).map((author) => `${author.firstName} ${author.lastName}`).join(',')} onChange={handleAuthorsChange} />

      <button type="submit">Add Book</button>
    </form>
  );
};

export default AddBookForm;
