import React, { useState } from 'react';
import axios from 'axios';

const AddBookForm = () => {
  const [book, setBook] = useState({
    title: '',
    authors: new Set(),
    genres: new Set(),
    publisher: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setBook({ ...book, [name]: value });
  };

  const handleAuthorsChange = (e) => {
    const authorsInput = e.target.value;
    const authorsArray = authorsInput.split(',').map((author) => {
      const [firstName, ...rest] = author.trim().split(' ');
      const lastName = rest.join(' ').trim();
      
      // Check if both first and last names are present
      if (firstName && lastName) {
        return { firstName, lastName };
      }
  
      // Handle the case when either first or last name is missing
      return null;
    });
  
    // Remove null entries and create a set
    const validAuthors = new Set(authorsArray.filter((author) => author !== null));
  
    setBook({ ...book, authors: validAuthors });
  };

  const handleGenresChange = (e) => {
    const genres = e.target.value.split(',').map((genre) => genre.trim());
    setBook({ ...book, genres: new Set(genres) });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Create a single DTO for the entire Book entity
      const bookDto = {
        title: book.title,
        authors: Array.from(book.authors),
        genres: Array.from(book.genres),
        publisher: book.publisher,
      };

      // Send a POST request with the Book DTO
      await axios.post('http://localhost:8080/book/add', bookDto, { headers: { 'Content-Type': 'application/json' } });

      // Clear the form after successful submission
      setBook({
        title: '',
        authors: new Set(),
        genres: new Set(),
        publisher: '',
      });

      console.log('Book added successfully!');
    } catch (error) {
      console.error('Error adding book:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>Title:</label>
      <input type="text" name="title" value={book.title} onChange={handleChange} />

      <label>Authors (comma-separated, first and last name):</label>
      <input type="text" name="authors" value={Array.from(book.authors).map((author) => `${author.firstName} ${author.lastName}`).join(',')} onChange={handleAuthorsChange} />

      <label>Genres (comma-separated):</label>
      <input type="text" name="genres" value={Array.from(book.genres).join(',')} onChange={handleGenresChange} />

      <label>Publisher:</label>
      <input type="text" name="publisher" value={book.publisher} onChange={handleChange} />

      <button type="submit">Add Book</button>
    </form>
  );
};

export default AddBookForm;
