// App.js
import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import LoginPage from './components/LoginPage';
import AddBookForm from './components/AddBookForm';
import AppBar from './components/AppBar';
import BookList from './components/BookList'; 
import IndividualBookPage from './components/IndividualBookPage';
import { AuthProvider } from './components/AuthContext';

const Home = () => <div>Home Page</div>;

const App = () => {
  return (
    <Router>
      <AuthProvider>
      <div>
        <AppBar />
        <Routes>
          <Route path="/login" element={<LoginPage />} />
          <Route path="/home" element={<Home />} />
          <Route path="/add-book" element={<AddBookForm />} />
          {/* Add a new route for the book list */}
          <Route path="/book-list" element={<BookList />} />
          {/* Add a new route for individual book pages */}
          <Route path="/book/:id" element={<IndividualBookPage />} />
          {/* Redirect to the login page if no matching route */}
          <Route path="/*" element={<Navigate to="/login" />} />
        </Routes>
      </div>
      </AuthProvider>
    </Router>
  );
};

export default App;
