import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const IndividualBookPage = () => {
  const { id } = useParams(); // Access the book ID from the URL parameters
  const [book, setBook] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [loadingReviews, setLoadingReviews] = useState(true);

  useEffect(() => {
    // Fetch individual book details based on the ID
    fetch(`http://localhost:8080/book/${id}`)
      .then(response => response.json())
      .then(data => setBook(data))
      .catch(error => console.error(`Error fetching book ${id} details:`, error));

    // Fetch reviews for the individual book using the updated endpoint
    fetch(`http://localhost:8080/review/book/${id}`)
      .then(response => response.json())
      .then(data => setReviews(data))
      .catch(error => console.error(`Error fetching reviews for book ${id}:`, error))
      .finally(() => setLoadingReviews(false));
  }, [id]);

  // Display a loading message while fetching data
  if (!book || loadingReviews) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h2>{book.title}</h2>
      <p>Author: {book.authors.map(author => `${author.firstName} ${author.lastName}`).join(', ')}</p>
      {book.publisher && (
        <p>
          Publisher: {book.publisher.name}
        </p>
      )}
      {/* Display book details */}
      
      <h3>Reviews:</h3>
      {reviews.length === 0 ? (
        <p>No reviews available for this book.</p>
      ) : (
        <ul>
          {reviews.map(review => (
            <li key={review.id}>
              <p>User ID: {review.user.username}</p>
              <p>Rating: {review.rating}</p>
              <p>{review.text}</p>
              <p>Date: {review.date}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default IndividualBookPage;
