// AppBar.js
import React from 'react';
import { AppBar as MuiAppBar, Toolbar, Button } from '@mui/material';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from './AuthContext'; // Update the path based on your project structure

const AppBar = () => {
  const { isLoggedIn, logout } = useAuth();
  const navigate = useNavigate();

  const handleLoginClick = () => {
    // Navigate to the login page when the "Login" button is clicked
    navigate('/login');
  };

  return (
    <MuiAppBar position="static">
      <Toolbar>
        <Button color="inherit" component={Link} to="/home">
          Home
        </Button>
        <Button color="inherit" component={Link} to="/add-book">
          Add Book
        </Button>
        {isLoggedIn ? (
          <Button color="inherit" onClick={logout}>
            Logout
          </Button>
        ) : (
          <Button color="inherit" onClick={handleLoginClick}>
            Login
          </Button>
        )}
      </Toolbar>
    </MuiAppBar>
  );
};

export default AppBar;
