import React from 'react';
import { AppBar as MuiAppBar, Toolbar, Button } from '@mui/material';
import { Link, useNavigate } from 'react-router-dom';

const AppBar = () => {
  // Replace with your actual authentication state and logout function
  const isLoggedIn = false; // Example: Replace with your authentication state
  const navigate = useNavigate();

  const handleLogout = () => {
    // Replace with your actual logout logic
    // For example, clearing authentication state and redirecting to the login page
    console.log('Logging out...');
    // Perform logout actions
    // Example: Clear authentication state, redirect to login page
    navigate('/login');
  };

  return (
    <MuiAppBar position="static">
      <Toolbar>
        <Button color="inherit" component={Link} to="/home">
          Home
        </Button>
        <Button color="inherit" component={Link} to="/add-book">
          Add Book
        </Button>
        {isLoggedIn ? (
          <Button color="inherit" onClick={handleLogout}>
            Logout
          </Button>
        ) : (
          <Button color="inherit" component={Link} to="/login">
            Login
          </Button>
        )}
      </Toolbar>
    </MuiAppBar>
  );
};

export default AppBar;
