import React, { useState } from 'react';
import axios from 'axios';

const AddBookForm = () => {
  const [book, setBook] = useState({
    title: '',
    authors: [],
    publisher: '',
    genres: [],
    newAuthor: {
      firstName: '',
      lastName: '',
    },
    newGenre: {
      name: '',
    },
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setBook((prevBook) => ({ ...prevBook, [name]: value }));
  };

  const handleAuthorChange = (e) => {
    const { name, value } = e.target;
    setBook((prevBook) => ({
      ...prevBook,
      newAuthor: {
        ...prevBook.newAuthor,
        [name]: value,
      },
    }));
  };

  const addAuthor = () => {
    setBook((prevBook) => ({
      ...prevBook,
      authors: [...prevBook.authors, { ...prevBook.newAuthor }],
      newAuthor: { firstName: '', lastName: '' },
    }));
  };

  const handleGenreChange = (e) => {
    const { value } = e.target;
    setBook((prevBook) => ({ ...prevBook, newGenre: { name: value } }));
  };

  const addGenre = () => {
    setBook((prevBook) => ({
      ...prevBook,
      genres: [...prevBook.genres, { ...prevBook.newGenre }],
      newGenre: { name: '' },
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Create a single DTO for the entire Book entity
      const bookDto = {
        title: book.title,
        authors: book.authors,
        publisher: book.publisher,
        genres: book.genres,
      };

      // Send a POST request with the Book DTO
      await axios.post('http://localhost:8080/book/add', bookDto, { headers: { 'Content-Type': 'application/json' } });

      // Clear the form after successful submission
      setBook({
        title: '',
        authors: [],
        publisher: '',
        genres: [],
        newAuthor: {
          firstName: '',
          lastName: '',
        },
        newGenre: {
          name: '',
        },
      });

      console.log('Book added successfully!');
    } catch (error) {
      console.error('Error adding book:', error);
    }
  };

  const isValidAuthorInput = () => {
    return book.newAuthor.firstName.trim() !== '' && book.newAuthor.lastName.trim() !== '';
  };

  const isValidGenreInput = () => {
    return book.newGenre.name.trim() !== '';
  };

  return (
    <div style={{ maxWidth : '400px',marginTop:'100px',marginLeft :'600px',marginRight :'300px',backgroundColor:'' }}>
      <h2>Add a book</h2>
    
    <form onSubmit={handleSubmit} >
      <label>Title:</label>
      <input type="text" name="title" value={book.title} onChange={handleChange} 
      style={{position:'relative',left:"110px"}}/>
      <br /><br />
      
      <div>
        <label>Author's First Name:</label>
        <input type="text" name="firstName" value={book.newAuthor.firstName} onChange={handleAuthorChange} required />
      </div>
      <br />
      <div>
        <label>Author's Last Name:</label>
        <input type="text" name="lastName" value={book.newAuthor.lastName} onChange={handleAuthorChange} required 
        style={{position:'relative',left:"2px"}}/>
      </div>
      <br />
      <button type="button" onClick={addAuthor}   
      disabled={!isValidAuthorInput()} style={{marginLeft : '235px'}}>
        Add Author
      </button>
      <br />
      <div style = {{ position : 'absolute',top : 220,left : 1000}}>
        <label>Authors:</label>
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(3, 1fr)', gap: '16px', marginRight:1}}>
          {book.authors.map((author, index) => (
            <div key={index} style={{ border: '1px solid #ccc', padding: '16px', borderRadius: '20px' }}>{`${author.firstName} ${author.lastName}`}</div>
          ))}
        </div>
      </div>
      <br />
      <div>
        <label>Publisher:</label>
        <input type="text" name="publisher" value={book.publisher} onChange={handleChange} 
        style={{position : 'relative',left:'75px'}} />
      </div>
      <br />
      <div>
        <label>Genre:</label>
        <input type="text" value={book.newGenre.name} onChange={handleGenreChange}
        style ={{position:'relative',left:'97px'}} 
        required/>
        <br /><br />
        <button type="button" onClick={addGenre}
        style ={{position : 'relative', left:'236px'}} 
        //disabled={!isValidGenreInput()}
        
        >
          Add Genre
        </button>
      </div>
      <br />
      <div style = {{ position : 'absolute',top : 820,left : 1000}}>
        <label>Genres:</label>
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(3, 1fr)', gap: '16px', marginRight:1}}>
          {book.genres.map((genre, index) => (
            <div key={index} style={{ border: '1px solid #ccc', padding: '16px', borderRadius: '20px' }}>{genre.name}</div>
          ))}
        </div>
      </div>

      <button type="submit">Add Book</button>
    </form>
    </div>
  );
};

export default AddBookForm;
