// AppBar.js
import React, { useEffect } from 'react';
import { AppBar as MuiAppBar, Toolbar, Button } from '@mui/material';
import { Link, useLocation, useHistory } from 'react-router-dom';
import { useAuth } from './AuthContext'; // Adjust the path accordingly

const AppBar = () => {
  const location = useLocation();
  const history = useHistory();
  const { user, logout, isAuthenticated } = useAuth(); // Add isAuthenticated from the AuthContext

  // Check if the current route is "/login"
  const isLoginPage = location.pathname === '/login';

  useEffect(() => {
    // Force a re-render when the authentication status changes
  }, [isAuthenticated]);

  const handleProfileClick = () => {
    // Redirect to the user profile page
    history.push('/profile');
  };

  if (isLoginPage) {
    return null; // Don't render AppBar on the login page
  }

  return (
    <MuiAppBar position="static">
      <Toolbar>
        <Button color="inherit" component={Link} to="/home">
          Home
        </Button>
        <Button color="inherit" component={Link} to="/add-book">
          Add Book
        </Button>
        <Button color="inherit" component={Link} to="/book-list">
          Book List
        </Button>
        {isAuthenticated ? (
          <div>
            {/* Button for user profile */}
            <Button color="inherit" onClick={handleProfileClick}>
              Profile
            </Button>
            {/* Logout button */}
            <Button color="inherit" onClick={logout}>
              Logout
            </Button>
          </div>
        ) : (
          <Button color="inherit" component={Link} to="/login">
            Login
          </Button>
        )}
      </Toolbar>
    </MuiAppBar>
  );
};

export default AppBar;
