import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useAuth } from './AuthContext';

const LoginPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const { login } = useAuth();
  const navigate = useNavigate();

  const handleLogin = async () => {
    try {
      // Authenticate against the API and get user details
      const response = await axios.post(
        'http://localhost:8080/user/findByUsername',
        {
          username,
        },
        {
          headers: {
            'Content-Type': 'application/json',
          },
        }
      );

      // Verify the password (assuming the API returns user details including the password)
      const user = response.data;

      if (user && user.password === password) {
        // Save user data in LocalStorage
        localStorage.setItem('username', username);
        localStorage.setItem('password', password);

        // Store user details in the context
        login(username);

        // Redirect to the AddBookForm page upon successful login
        navigate('/add-book');
      } else {
        // Incorrect credentials
        alert('Invalid credentials');
      }
    } catch (error) {
      console.error('Login failed:', error);
      alert('Error during login');
    }
  };

  return (
    <div>
      <h2>Login</h2>
      <label>Username:</label>
      <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} />
      <br />
      <label>Password:</label>
      <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
      <br />
      <button onClick={handleLogin}>Login</button>
    </div>
  );
};

export default LoginPage;
